mapper.select:
SELECT c.cmt_no, c.cmt_board_no, c.cmt_p_no, c.cmt_group, c.cmt_order, c.cmt_depth, c.cmt_writer, c.cmt_datetime, c.cmt_content, u.nickname AS cmt_nickname FROM tbl_comment c LEFT JOIN tbl_users u ON c.cmt_writer = u.username WHERE c.cmt_board_no = #{commentVO.cmt_board_no}
AND c.cmt_delete = 0
ORDER BY c.cmt_group DESC, c.cmt_order ASC LIMIT #{pageVO.limit} OFFSET #{pageVO.offset}

SELECT IFNULL(MAX(cmt_order),0)
FROM tbl_comment
WHERE cmt_group = #{cmt_group}

mapper.update:
UPDATE tbl_comment
SET
	cmt_delete = #{cmt_delete}
WHERE cmt_no = #{cmt_no}
